    public static Map<Short, Map<Integer,Integer>> init() {
        Map<Short, Map<Integer,Integer>> durability = new HashMap<>();

        for (int i = 0; i < TOOL_LIST.length; i++) {
            Map<Integer, Integer> dur = new HashMap<>();
            for (int j = 0; j < DURABILITY_LIST[i].length; j++) {
                dur.put(j, DURABILITY_LIST[i][j]);
            }
            for (int j = 0; j< DURABILITY_LIST_2[i].length; j++) {
                dur.put(j+100, DURABILITY_LIST[i][j]);
            }
            durability.put(TOOL_LIST[i], dur);
        }

        return durability;
    }
